import React from 'react';
import { motion } from 'framer-motion';
import { 
  Terminal, 
  Server, 
  Database, 
  Layout, 
  Code, 
  Cloud, 
  Briefcase, 
  GraduationCap, 
  Mail, 
  Github, 
  Linkedin 
} from 'lucide-react';

// --- DATA FROM RESUME ---
const portfolioData = {
  personal: {
    name: "Solomon Prabu D",
    role: "Software Engineer & DevOps Enthusiast",
    summary: "Delivering scalable applications using Python, JavaScript, and cloud-native technologies.",
    contact: "solomonprabu002@gmail.com",
    location: "Gothenburg, Sweden"
  },
  skills: [
    { name: "Python", icon: <Code size={20} /> },
    { name: "Docker & K8s", icon: <ContainerIcon /> }, // Custom icon below
    { name: "Azure & GCP", icon: <Cloud size={20} /> },
    { name: "Odoo ERP", icon: <Database size={20} /> },
    { name: "React & JS", icon: <Layout size={20} /> },
    { name: "CI/CD", icon: <Server size={20} /> },
  ],
  experience: [
    {
      company: "Zriya Solutions AB",
      role: "System Engineer",
      period: "Sep 2023 - Present",
      desc: "Managing client relationships and technical consulting. Streamlined vehicle booking systems for Zeekrtech Europe.",
      tags: ["Power Apps", "Python", "Azure"]
    },
    {
      company: "Zeekrtech Europe (Consultant)",
      role: "O365 & Python Dev",
      period: "Sep 2023 - Jun 2025",
      desc: "Reduced manual work by 80% via Power Automate. Developed workshop time-tracking to boost productivity by 30%.",
      tags: ["Automation", "Docker", "Kubernetes"]
    },
    {
      company: "OODU Implementers",
      role: "Technical Consultant",
      period: "Jun 2022 - Aug 2023",
      desc: "Designed tailored Odoo ERP solutions with third-party integrations and improved operational workflows.",
      tags: ["Odoo", "ERP", "Python"]
    }
  ],
  projects: [
    {
      title: "Fleet Management ERP",
      desc: "Bespoke ERP modules deployed as containerized apps using Docker & Kubernetes on Azure. Saved 6hrs weekly on deployments.",
      tech: "Odoo, Docker, Azure"
